function downloadChartPDF(chart, fileName) {
    var pdfWidth = 595.28; 
    var pdfHeight = 841.89; 

    // Hitung faktor skala
    var scaleFactor = pdfWidth / chart.width;

    // Buat dokumen PDF dengan ukuran A4
    var pdf = new jsPDF('p', 'pt', 'a4');
    pdf.addImage(chart.toDataURL('image/png'), 'PNG', 0, 0, chart.width * scaleFactor, chart.height * scaleFactor);
    pdf.save(fileName + '.pdf');
}

function downloadChartPNG(chart, fileName) {
    var link = document.createElement('a');
    link.href = chart.toDataURL('image/png');
    link.download = fileName;
    link.click();
}